import React, { useState } from 'react';
import Calendar from 'react-calendar';
import 'react-calendar/dist/Calendar.css'; // Import calendar styles

const currencyCodeMap = {
  Pounds: "GBP",
  Dollars: "USD",
  Euros: "EUR",
  Shekels: "ILS",
};

function MyCalendar({ activeMenu }) {
  const [fromCurrency, setFromCurrency] = useState("");
  const [toCurrency, setToCurrency] = useState("");
  const [data, setData] = useState(null);
  const [selectedDate, setSelectedDate] = useState(new Date());
  const currencies = ["GBP", "USD", "EUR", "ILS"];
  
  const handleDateChange = date => {
    setSelectedDate(date); // עדכון המשתנה כאשר המשתמש מבחין תאריך
  };

  const handleSubmit = async () => {
    try{
      fetch('http://localhost:3001/rates',{
        method: 'POST',
        headers: { 'Content-Type': 'application/json'},
        body: JSON.stringify({fromCurrency,toCurrency, selectedDate})
      })
      .then((res) => res.json())
      .then((data) => {
        con
        setData(data.results);
      });
    }
    catch(err) {console.log(err);}
  };

  return (
    activeMenu === "Historical_and_current_rates" ? (
      <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center', justifyContent: 'flex-start', height: '100vh' }}>
        <br></br>
        <Calendar
          value={selectedDate}
          onChange={handleDateChange}
        />
        <br></br>
        <div style={{color: 'white', fontSize: '20px'}}>
          <p>Selected date: {selectedDate.toLocaleDateString()} </p>
          </div>
          <br></br>
       <div style={{color: 'white', fontSize: '20px'}}>
          <label htmlFor="fromCurrency">Convert from: </label>
          <select
            id="fromCurrency"
            value={fromCurrency}
            onChange={(e) => setFromCurrency(e.target.value)}
          >
            <option value="">Select Currency</option>
            {currencies
              .filter((currency) => currency !== toCurrency)
              .map((currency) => (
                <option key={currency} value={currency}>
                  {currency}
                </option>
              ))}
          </select>
        <br></br>
        <br></br>
        <div style={{color: 'white', fontsize: '20px'}}>
          <label htmlFor="toCurrency">Convert to: </label>
          <select
            id="toCurrency"
            value={toCurrency}
            onChange={(e) => setToCurrency(e.target.value)}
          >
            <option value="">Select Currency</option>
            {currencies
              .filter((currency) => currency !== fromCurrency)
              .map((currency) => (
                <option key={currency} value={currency}>
                  {currency}
                </option>
              ))}
          </select>
        </div>
        </div>
        <br></br>
        <form>
        <input type="submit" value="Submit" onClick={handleSubmit} />
        {data && (
          <div style={{marginTop: '20px', color: 'white'}}>
            <p>{data}</p>
          </div>
        )}
        </form>

       </div> 
    ) : null
  )
}
export default MyCalendar;
