import React, { useState } from 'react';
import Calendar from 'react-calendar';
import 'react-calendar/dist/Calendar.css'; // Import calendar styles

function MyCalendar({ activeMenu }) {
  const [from, setFrom] = useState("");
  const [to, setTo] = useState("");
  const [apiData, setApiData] = useState(null);
  const [selectedDate, setSelectedDate] = useState(new Date());
  const currencies = ["GBP", "USD", "EUR", "ILS"];
 

  const handleSubmit = async () => {
    fetch('http://localhost:3001/data',{
        method: 'POST',
        headers: { 'Content-Type': 'application/json'},
        body: JSON.stringify()
      })
  };

  return (
    activeMenu === "Historical_and_current_rates" ? (
      <div style={{ display: 'flex', 
          flexDirection: 'column', alignItems: 'center', 
          justifyContent: 'flex-start', height: '100vh' }}>
        <br></br>
        <Calendar
          onChange={(e) => setSelectedDate(e.target.value)}
          value={selectedDate}
        />
        <br></br>
        <div style={{color: 'white', fontSize: '20px'}}>
          <p>Selected date: {selectedDate.to()} </p>
          </div>
          <br></br>
       <div style={{color: 'white', fontSize: '20px'}}>
          <label htmlFor="fromCurrency">Convert from: </label>
          <select
            id="fromCurrency"
            value={from}
            onChange={(e) => setFrom(e.target.value)}
          >
            <option value="">Select Currency</option>
            {currencies
              .filter((currency) => currency !== to)
              .map((currency) => (
                <option key={currency} value={currency}>
                  {currency}
                </option>
              ))}
          </select>
        <br></br>
        <br></br>
        <div style={{color: 'white', fontsize: '20px'}}>
          <label htmlFor="toCurrency">Convert to: </label>
          <select
            id="toCurrency"
            value={to}
            onChange={(e) => setSelectedDate(e.target.value)}
          >
            <option value="">Select Currency</option>
            {currencies
              .filter((currency) => currency !== from)
              .map((currency) => (
                <option key={currency} value={currency}>
                  {currency}
                </option>
              ))}
          </select>
        </div>
        </div>
        <br></br>
        <form onSubmit={MyCalendar}>
        <input type="submit" value="Submit" onClick={handleSubmit} />
        {apiData && (
          <div style={{marginTop: '20px', color: 'white'}}>
            <strong>Value: </strong> {apiData}
          </div>
        )}
        </form>

       </div> 
    ) : null
  )
}
export default MyCalendar;
